import React, { useState } from 'react';
const Contact = () => {

    const [data, setData] = useState({
        fullname: '',
        phone: '',
        email: '',
        msg: ''
    });

    const InputEvent = (event) => {
        const {name, value} = event.target;
        setData((prevValue)=>{
            return {
                ...prevValue,
                [name]: value
            }
        })
    }

    const fromSubmit = (event) => {
        event.preventDefault();
        alert(`My Name is ${data.fullname}. My Phone Number is ${data.phone}. ${data.email}  is my email.
        here I wnat to Say ${data.msg}
        `);
    }

    return (
        <>
        <div className="my-5">
            <h1 className="text-center">Contact US</h1>
        </div>
        <div className="container contact_div">
            <div className="row">
                <div className="col-md-6 col-10 mx-auto">
                    <form onSubmit={fromSubmit}>
                        <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Enter Your Full Name</label>
                            <input 
                                type="text" 
                                className="form-control" 
                                id="exampleFormControlInput1"
                                name="fullname"
                                value={data.fullname}
                                onChange={InputEvent}
                                placeholder="Enter Your Full Name" 
                            />
                        </div>
                        <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Phone Number</label>
                            <input 
                                type="number" 
                                className="form-control" 
                                id="exampleFormControlInput1"
                                name="phone"
                                value={data.phone}
                                onChange={InputEvent}
                                placeholder="Enter Your Phone Number" 
                            />
                        </div>
                        <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Email address</label>
                            <input 
                                type="email" 
                                className="form-control" 
                                id="exampleFormControlInput1"
                                name="email"
                                value={data.email}
                                onChange={InputEvent}
                                placeholder="name@example.com" 
                            />
                        </div>
                        <div className="mb-3">
                        <label for="exampleFormControlTextarea1" className="form-label">Message</label>
                        <textarea 
                            name="msg"
                            value={data.msg}
                            onChange={InputEvent}
                            className="form-control" 
                            id="exampleFormControlTextarea1" 
                            rows="3">
                        </textarea>
                    </div>
                    <div class="col-12">
                     <button class="btn btn-outline-primary" type="submit">Submit form</button>
                    </div>
                    </form>
                </div>
            </div>
        </div>
        </>
    )
}
export default Contact;
